  }

  componentDidMount() {
    document.addEventListener("scroll", () => {
      const backgroundcolor = window.scrollY < 100 ? "white!important" : "white!important";
      console.log(backgroundcolor);


      this.setState({ navBackground: {backgroundcolor: backgroundcolor} });
    });
  } */

  render() {
    return (
        <nav id="menu" className="navbar navbar-default navbar-fixed-top">
        <div className="container"> 
          <div className="navbar-header">
            <button type="button" className="navbar-toggle collapsed" data-toggle="collapse" data-target="#bs-example-navbar-collapse-1"> <span className="sr-only">Toggle navigation</span> <span className="icon-bar"></span> <span className="icon-bar"></span> <span className="icon-bar"></span> </button>
            <a className="navbar-brand page-scroll" href="#page-top">PhnomCode</a> </div>

          <div className="collapse navbar-collapse" id="bs-example-navbar-collapse-1">
            <ul className="nav navbar-nav navbar-right">
              <Link
                activeClass="active"
                to="section1"
                spy={true}
                smooth={true}
                offset={-70}
                duration= {500}>Who are we?
              </Link>
              <li><a href="#missions" className="page-scroll">Who are we?</a></li>
              <li><a href="#about" className="page-scroll">About</a></li>
              <li><a href="#personality" className="page-scroll">Products</a></li>
              <li><a href="#portfolio" className="page-scroll">Pricing</a></li>
              <li><a href="#testimonials" className="page-scroll">Testimonials</a></li>
              <li><a href="#team" className="page-scroll">Team</a></li>
              <li><a href="#contact" className="page-scroll">Contact</a></li>
            </ul>
          </div>
        </div>
      </nav>
    )
  }
}

export default Navigation
